    opacity: 0.47,
    fontSize: 18,
    marginLeft: 21,
    marginTop: 13
  },
  phoneinput: {
    width: 322,
    height: 50,
    backgroundColor: "rgba(255,255,255,1)",
    marginTop: 15,
    marginLeft: 27
